var source = new Tuple<int, int>[] { new Tuple<int, int>(){ 1 , 2 },
                                        new Tuple<int, int>(){ 3 , 4 },
                                        new Tuple<int, int>(){ 5 , 6 },
                                        new Tuple<int, int>(){ 7 , 8 },
                                        new Tuple<int, int>(){ 9 , 10 } };
var minValue = source.Scalar(2, //Two fields (Item1 and Item2 within Tuple)
                                (tupleItem, accessorArray) => { accessorArray[0] = tupleItem.Item1; accessorArray[1] = tupleItem.Item2; return true },
                                (minValue, tupleValue) => Math.Min(minValue, tupleValue) );

var maxValue = source.Scalar(2, //Two fields (Item1 and Item2 within Tuple)
                                (tupleItem, accessorArray) => { accessorArray[0] = tupleItem.Item1; accessorArray[1] = tupleItem.Item2; return true },
                                (minValue, tupleValue) => Math.Max(minValue, tupleValue) );

