string dataType;
int dimensions;
int[] dimensionSizes;

IsDataTypeArray("int[,]", out dataType, out dimensions, out dimensionSizes)
	//Returns true, dataType: int, dimensions: 2, dimensionSizes: null

IsDataTypeArray("int[2,3]", out dataType, out dimensions, out dimensionSizes)
	//Returns true, dataType: int, dimensions: 2, dimensionSizes: {2,3}

IsDataTypeArray("int[23]", out dataType, out dimensions, out dimensionSizes)
	//Returns true, dataType: int, dimensions: 1, dimensionSizes: {23}

IsDataTypeArray("int[]", out dataType, out dimensions, out dimensionSizes)
	//Returns true, dataType: int, dimensions: 1, dimensionSizes: null

IsDataTypeArray("int", out dataType, out dimensions, out dimensionSizes)
	//Returns false, dataType: null, dimensions: int.MinValue, dimensionSizes: null

var newAnonymousValues = new object[] { "Item1Str", 1, (long) 2, new DateTime(2012, 12, 14, 9, 34, 35) };
var newAnonymousType = TypeHelpers.CreateAnonymousInstance(new { Item1=String.Empty, Item2=int.MinValue, Item3=long.MinValue, Item4=new System.Nullable<DateTime>() },
															newAnonymousValues);

/// newAnonymousType now have the following Values:
///		newAnonymousType.Item1 => "Item1Str"
///		newAnonymousType.Item2 => 1
///		newAnonymousType.Item3 => 2
///		newAnonymousType.Item4 => #12/14/2012 9:34:35#

    public interface AI
    {}

    public class A1 : AI
    {}

    public class A2 : AI
    {}

    public class A11 : A1
    {}

    public class B
    {}

    public class AG<T> : AI
    {}

    void Main()
    {
        var testInstance = new A1();

        TypeSwitch.Test(testInstance,
                         TypeSwitch.Case<A1>(() => Console.WriteLine("Is of this Type")),
                         TypeSwitch.Case<AI>(() => Assert.IsTrue(false, "AI case should NOT have been Triggered")),
                         TypeSwitch.Default(() => Assert.IsTrue(false, "Default Case should NOT have been Triggered"))
                        );

        TypeSwitch.Test(testInstance,
                                TypeSwitch.Case<A11>(() => Assert.IsTrue(false, "A11 case should NOT have been Triggered")),
                                TypeSwitch.Case<AI>(() => Console.WriteLine("Is of this Type")),
                                TypeSwitch.Default(() => Assert.IsTrue(false, "Default Case should NOT have been Triggered"))
                                );

        testInstance = new A11();

        TypeSwitch.Test(testInstance,
                               TypeSwitch.Case<A1>(() => Assert.IsTrue(true)),
                                TypeSwitch.Case<A11>(() => Assert.IsTrue(false, "A11 case should NOT have been Triggered, since it is after the A1 case")),
                                TypeSwitch.Case<AI>(() => Assert.IsTrue(false, "AI case should NOT have been Triggered")),
                                TypeSwitch.Default(() => Assert.IsTrue(false, "Default Case should NOT have been Triggered"))
                                );

        TypeSwitch.Test(testInstance,
                                TypeSwitch.ExplicitCase<A1>(() => Assert.IsTrue(false, "A1 case should NOT have been Triggered, must be the same type.")),
                                TypeSwitch.ExplicitCase<A11>(() => Assert.IsTrue(true)),
                                TypeSwitch.ExplicitCase<AI>(() => Assert.IsTrue(false, "AI case should NOT have been Triggered")),
                                TypeSwitch.Default(() => Assert.IsTrue(false, "Default Case should NOT have been Triggered"))
                                );

        AI testInstance4 = new AG<int>();

        TypeSwitch.Test(testInstance4,
                                TypeSwitch.Case<A11>(() => Assert.IsTrue(false, "A11 case should NOT have been Triggered")),
                                TypeSwitch.Case<AI>(() => Assert.IsTrue(true)),
                                TypeSwitch.Default(() => Assert.IsTrue(false, "Default Case should NOT have been Triggered"))
                                );

        TypeSwitch.Test(testInstance4,
                                TypeSwitch.Case<AG<AI>>(() => Assert.IsTrue(false, "AG case should NOT have been Triggered")),
                                TypeSwitch.ExplicitCase<AI>(() => Assert.IsTrue(false, "AI case should NOT have been Triggered")),
                                TypeSwitch.Case<AG<int>>(() => Assert.IsTrue(true)),
                                TypeSwitch.Default(() => Assert.IsTrue(false, "Default Case should NOT have been Triggered"))
                                );

        var testInstance6 = new int[] { 1, 2, 3 };

        TypeSwitch.Test(testInstance6,
                          TypeSwitch.Case<int[,]>(() => Assert.IsTrue(false, "int[,] case should NOT have been Triggered")),
                          TypeSwitch.Case<int[]>(() => Assert.IsTrue(true)),
                          TypeSwitch.Default(() => Assert.IsTrue(false, "Default Case should NOT have been Triggered"))
                          );

        TypeSwitch.Test(testInstance6,
                          TypeSwitch.ExplicitCase<Array>(() => Assert.IsTrue(false, "Array Explicit case should NOT have been Triggered")),
                          TypeSwitch.Case<Array>(() => Assert.IsTrue(true)),
                          TypeSwitch.Default(() => Assert.IsTrue(false, "Default Case should NOT have been Triggered"))
                          );
}
